package com.zjrb.sjzsw.ui.fragment;import android.os.Bundle;import android.support.annotation.Nullable;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.Button;import com.zjrb.sjzsw.R;import butterknife.BindView;import butterknife.ButterKnife;import butterknife.OnClick;import butterknife.Unbinder;/** * 类描述： * * @author jinzifu * @Email jinzifu123@163.com * @date 2018/3/5 1418 */public class AudioRecordFragment extends BaseFragment {    @BindView(R.id.start_aduio)    Button startAduio;    Unbinder unbinder;    @Override    protected int getLayoutId() {        return R.layout.fr_audiorecord;    }    @Override    protected void init(@Nullable Bundle savedInstanceState) {    }    @Override    public View onCreateView(LayoutInflater inflater, ViewGroup container, Bundle savedInstanceState) {        View rootView = super.onCreateView(inflater, container, savedInstanceState);        unbinder = ButterKnife.bind(this, rootView);        return rootView;    }    @Override    public void onDestroyView() {        super.onDestroyView();        unbinder.unbind();    }    @OnClick(R.id.start_aduio)    public void onViewClicked() {        showToast("开始录制");    }}