package com.zjrb.sjzsw.ui.fragment;import android.os.Bundle;import android.support.annotation.Nullable;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import com.zjrb.sjzsw.R;import butterknife.ButterKnife;import butterknife.OnClick;import butterknife.Unbinder;/** * 类描述：自定义view * * @author jinzifu * @Email jinzifu123@163.com * @date 2018/3/29 1403 */public class CustomViewFragment extends BaseFragment {    Unbinder unbinder;    @Override    protected int getLayoutId() {        return R.layout.fr_customview;    }    @Override    protected void init(@Nullable Bundle savedInstanceState) {    }    @Override    public View onCreateView(LayoutInflater inflater, ViewGroup container, Bundle savedInstanceState) {        View rootView = super.onCreateView(inflater, container, savedInstanceState);        unbinder = ButterKnife.bind(this, rootView);        return rootView;    }    @Override    public void onDestroyView() {        super.onDestroyView();        unbinder.unbind();    }    @OnClick({R.id.custom_button, R.id.frame_layout, R.id.root_layout})    public void onViewClicked(View view) {        switch (view.getId()) {            case R.id.custom_button:                showToast("您点击的button");                break;            case R.id.frame_layout:                showToast("您点击的frameLayout");                break;            case R.id.root_layout:                showToast("您点击的rootLayout");                break;            default:                break;        }    }}