package com.zjrb.sjzsw.widget;import android.content.Context;import android.support.annotation.Nullable;import android.util.AttributeSet;import android.util.Log;import android.view.MotionEvent;/** * 类描述：自定义 * * @author jinzifuButto * @Email jinzifu123@163.com * @date 2018/3/29 1530 */public class CustomButton extends android.support.v7.widget.AppCompatButton {    private final String TAG = "CustomButton";    public CustomButton(Context context) {        super(context);    }    public CustomButton(Context context, @Nullable AttributeSet attrs) {        super(context, attrs);    }    public CustomButton(Context context, @Nullable AttributeSet attrs, int defStyleAttr) {        super(context, attrs, defStyleAttr);    }    @Override    public boolean dispatchTouchEvent(MotionEvent ev) {        Log.d(TAG, "dispatchTouchEvent= super.dispatchTouchEvent(ev)");        return super.dispatchTouchEvent(ev);    }    @Override    public boolean onTouchEvent(MotionEvent event) {        switch (event.getAction()) {            case MotionEvent.ACTION_DOWN:                Log.d(TAG, "onTouchEvent= ACTION_DOWN)");                break;            case MotionEvent.ACTION_MOVE:                break;            case MotionEvent.ACTION_UP:                Log.d(TAG, "onTouchEvent= ACTION_UP)");                break;            default:                break;        }        return super.onTouchEvent(event);    }}