package com.zjrb.sjzsw.widget;import android.content.Context;import android.support.annotation.AttrRes;import android.support.annotation.NonNull;import android.support.annotation.Nullable;import android.util.AttributeSet;import android.util.Log;import android.view.MotionEvent;import android.widget.FrameLayout;/** * 类描述：自定义Framelayout * * @author jinzifu * @Email jinzifu123@163.com * @date 2018/3/29 1539 */public class CustomFrameLayout extends FrameLayout {    private final String TAG = "CustomFrameLayout";    public CustomFrameLayout(@NonNull Context context) {        super(context);    }    public CustomFrameLayout(@NonNull Context context, @Nullable AttributeSet attrs) {        super(context, attrs);    }    public CustomFrameLayout(@NonNull Context context, @Nullable AttributeSet attrs, @AttrRes int defStyleAttr) {        super(context, attrs, defStyleAttr);    }    @Override    public boolean dispatchTouchEvent(MotionEvent ev) {        Log.d(TAG, "dispatchTouchEvent= super.dispatchTouchEvent(ev)");        return super.dispatchTouchEvent(ev);    }    @Override    public boolean onInterceptTouchEvent(MotionEvent ev) {        Log.d(TAG, "onInterceptTouchEvent= super.onInterceptTouchEvent(ev)");        return super.onInterceptTouchEvent(ev);    }    @Override    public boolean onTouchEvent(MotionEvent event) {        Log.d(TAG, "onTouchEvent= super.onTouchEvent(ev)");        return super.onTouchEvent(event);    }}